def next_generation(pops, W, C, V, POP_SIZE, N_P):
    # {Create a new population using deterministic tournament selection, single-point crossover, and a heuristic-based mutation that favors items with higher value-to-weight ratios.}

    # Initialize the new population
    new_pops = np.zeros((POP_SIZE, N_P), dtype=np.int32)

    for i in range(POP_SIZE):
        # Deterministic tournament selection
        competitors = np.random.choice(POP_SIZE, 2, replace=False)
        if pops['rankings'][competitors[0]] < pops['rankings'][competitors[1]]:
            winner = pops['individuals'][competitors[0]]
        else:
            winner = pops['individuals'][competitors[1]]

        # Single-point crossover
        point = np.random.randint(1, N_P)
        if i > 0:  # Ensure we have at least one parent from the previous generation
            parent = new_pops[np.random.randint(i)]
            child = np.concatenate((winner[:point], parent[point:]))
        else:
            child = winner.copy()  # The first individual is a copy of the winner

        # Heuristic-based mutation
        value_weight_ratio = V.sum(axis=1) / W
        sorted_indices = np.argsort(value_weight_ratio)[::-1]
        for j in sorted_indices:
            if child[j] == 0 and np.dot(child, W) + W[j] <= C:
                child[j] = 1  # Add item if it improves the value without exceeding weight
            elif child[j] == 1 and np.random.rand() < 0.05:
                child[j] = 0  # Randomly remove item with a small probability

        # Ensure the child meets the weight constraint
        while np.dot(child, W) > C:
            overweight_indices = np.where(child == 1)[0]
            child[np.random.choice(overweight_indices)] = 0

        # Add the child to the new population
        new_pops[i] = child

    return new_pops
